import React from "react";
import "./shop.css";
// 引入shop page 的 data
import shareShop from "../../data/data";
import { Link } from "react-router-dom";

export default function Shop() {
  let shopData = shareShop();
  console.log(shopData);
  return (
    <>
      <div className="shopPageProducts">
        <ul className="ulShopProducts">
          {shopData.map((item, index) => {
            return (
              <Link to={`/product/${item.parameter}`} key={index}>
                <li className="listProduct" key={item.id}>
                  <div className="productImgShop">
                    <img src={item.img} alt="" />
                  </div>
                  <div className="productNameShop">
                    <h2>{item.name}</h2>
                  </div>
                  <div className="productBrandShop">
                    <p>{item.brand}</p>
                  </div>
                  <div className="productPrice">
                    <p>$ {item.price}</p>
                  </div>
                </li>
              </Link>
            );
          })}
        </ul>
      </div>
    </>
  );
}
