// 从actionTypes里面引入addToCart
import { addToCart } from "../Action/ActionTypes";
// 引入 data
import shareShop from "../../data/data";
let data = shareShop();
let product = {
  products: data,
  cartItems: [],
};
// 定义reducer函数，reducer函数里面有两个参数 1. state  2. action
let rootReducer = (state = product, action) => {
  switch (action.type) {
    case "addToCart":
      // 解构商品的属性
      let { id, name, brand, desc, price, img, size, color, parameter } =
        state.products.find((product) => product.id == action.payload);
      // 判断购物车里是否有该商品，如果有，则让其数量+1，如果没有添加进去，用来当作判断的 true or false
      let existingItem = state.cartItems.find((item) => ListItem.id == id);
      if (existingItem) {
        return {
          // 如果存在该商品，则让该商品的数量 +1
          ...state,
          cartItems: state.cartItems.map((item) =>
            item.id == id ? { ...item, quantity: item.quantity + 1 } : item
          ),
        };
      } else {
        //   如果没有该商品时
        return {
          ...state,
          cartItems: [...state.cartItems, { name, brand, price, quantity: 1 }],
        };
      }

    default:
      return state;
  }
};
// 暴露出rootReducer
export default rootReducer;
