import React from "react";
import "./productDetails.css";
// 获取传递的参数 params
// 引入useParams
import { useParams } from "react-router-dom";
import { getData } from "../../data/data";

export default function ProductDetails() {
  let params = useParams();
  console.log(params);
  // 属性值params.productId
  // 拿到商品详情页的数据
  let productDatas = getData(params.productId);
  // console.log(datas);
  return (
    <>
      <div className="productDetails">
        <div className="detailsShow">
          <div className="backToShop">Back to shop</div>
          <div className="productInfo">
            <div className="productInfoLeft">
              <div className="productInfoLeftImg">{productDatas.img}</div>
            </div>
            <div className="productInfoMiddle">
              <div className="productInfoMiddleImg">{productDatas.img}</div>
            </div>
            <div className="productInfoRight"></div>
          </div>
        </div>
      </div>
    </>
  );
}
