import * as React from "react";
import Box from "@mui/material/Box";
import SwipeableDrawer from "@mui/material/SwipeableDrawer";
import Button from "@mui/material/Button";
import List from "@mui/material/List";
import Divider from "@mui/material/Divider";
import ListItem from "@mui/material/ListItem";
import ListItemButton from "@mui/material/ListItemButton";
import ListItemIcon from "@mui/material/ListItemIcon";
import ListItemText from "@mui/material/ListItemText";
// 引入购物车icon组件
import CustomizedBadges from "./ CustomizedBadges";
// 引入useSelector 来拿到store里面的数据
import { useSelector } from "react-redux";
// 引入样式
import "./drawer.css";

export default function SwipeableTemporaryDrawer() {
  // 通过引入useSelector 来拿到store里面的数据
  // 购物车里面的数据
  let products = useSelector((state) => state.cartItems);
  const [state, setState] = React.useState({
    right: false,
  });

  const toggleDrawer = (anchor, open) => (event) => {
    if (
      event &&
      event.type === "keydown" &&
      (event.key === "Tab" || event.key === "Shift")
    ) {
      return;
    }

    setState({ ...state, [anchor]: open });
  };

  const list = (anchor) => (
    <Box
      sx={{ width: anchor === "top" || anchor === "bottom" ? "auto" : 250 }}
      role="presentation"
      onClick={toggleDrawer(anchor, false)}
      onKeyDown={toggleDrawer(anchor, false)}
      className="box1"
    >
      {/* list里面是购物车里面展示的数据 */}
      <div className="box">
        <div className="basket">
          <div className="left">
            <h1>
              My Basket <span>({`${products.length} items`})</span>
            </h1>
          </div>
          <div className="right">
            <div className="close">Close</div>
            <div className="clearBasket">Clear Basket</div>
          </div>
        </div>
        <ul>
          {products.map((item, index) => {
            return (
              <li key={index}>
                <h1>{item.name}</h1>
                <h2>{item.brand}</h2>
                <h3>{item.quantity}</h3>
              </li>
            );
          })}
        </ul>
      </div>
    </Box>
  );

  return (
    <div>
      {["right"].map((anchor) => (
        <React.Fragment key={anchor}>
          <Button onClick={toggleDrawer(anchor, true)}>
            {/* // 引入购物车icon组件 */}
            <CustomizedBadges></CustomizedBadges>
          </Button>
          <SwipeableDrawer
            anchor={anchor}
            open={state[anchor]}
            onClose={toggleDrawer(anchor, false)}
            onOpen={toggleDrawer(anchor, true)}
          >
            {list(anchor)}
          </SwipeableDrawer>
        </React.Fragment>
      ))}
    </div>
  );
}
//需要拿到购物车里面的数据，然后才能拿到购物车里cartItems的数量
